option.value:
import React from "react";
import { View, Text, StyleSheet } from "react-native";
import Svg, { Defs, Stop, LinearGradient, Path } from "react-native-svg";
import { Colors } from '@/constants/Colors';

interface CircularGaugeProps {
  percentage: number;
  size?: number;
  strokeWidth?: number;
  pieGradientColors?: string[];
  centerCircleColor?: string;
  percentageTextColor?: string;
  percentageTextSize?: number;
  percentageTextWeight?: 'normal' | 'bold' | '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
}

export default function CircularGauge({
  percentage,
  size = 72,
  strokeWidth = 12,
  pieGradientColors = ['#BD0000', '#FF8282'], // Default gradient colors
  centerCircleColor = Colors.light.background, // Default white
  percentageTextColor = Colors.light.text, // Default dark text
  percentageTextSize = 20, // Default font size
  percentageTextWeight = 'bold', // Default font weight
}: CircularGaugeProps) {
  // 게이지의 중심과 반지름 계산
  const center = size / 2;
  const radius = center - strokeWidth / 2;

  // 파이(피자) 모양 게이지를 위한 각도 계산
  const startAngle = -90;
  const endAngle = startAngle + (percentage / 100) * 360;
  const bgPath = describeArc(center, center, radius, endAngle, startAngle + 360);
  const radians = (deg: number) => (deg * Math.PI) / 180;
  const describeArc = (cx: number, cy: number, r: number, startAngle: number, endAngle: number) => {
    const start = {
      x: cx + r * Math.cos(radians(startAngle)),
      y: cy + r * Math.sin(radians(startAngle)),
    };
    const end = {
      x: cx + r * Math.cos(radians(endAngle)),
      y: cy + r * Math.sin(radians(endAngle)),
    };
    const largeArcFlag = endAngle - startAngle <= 180 ? "0" : "1";
    return [
      `M ${cx} ${cy}`,
      `L ${start.x} ${start.y}`,
      `A ${r} ${r} 0 ${largeArcFlag} 1 ${end.x} ${end.y}`,
      "Z",
    ].join(" ");
  };
  const piePath = describeArc(center, center, radius, startAngle, endAngle);

  return (
    <View style={[styles.container, { width: size, height: size, borderRadius: size / 2 }]}>
      <Svg width={size} height={size} style={{ position: 'absolute' }}>
        <Defs>
          {/* 밝은 부분이 절대적 상단(12시 방향)에 오도록 x1/y1=0.5,0 x2/y2=0.5,1로 설정 */}
          <LinearGradient id="strokeGradient" x1="50%" y1="0%" x2="50%" y2="100%">
            {pieGradientColors.map((color, index) => (
              <Stop key={index} offset={`${(index / (pieGradientColors.length - 1)) * 100}%`} stopColor={color} />
            ))}
          </LinearGradient>
        </Defs>
        {/* 비어있는 부분 배경 (회색) */}
        <Path
          d={bgPath}
          fill="#EFEFEF"
        />
        {/* 파이 모양 게이지 */}
        <Path
          d={piePath}
          fill="url(#strokeGradient)"
        />
      </Svg>
      {/* 중앙 흰색 원 */}
      <View style={{
        position: 'absolute',
        width: size * 0.6,
        height: size * 0.6,
        left: size * 0.2,
        top: size * 0.2,
        borderRadius: size * 0.3,
        backgroundColor: centerCircleColor,
        alignItems: 'center',
        justifyContent: 'center',
        shadowColor: Colors.light.text,
        shadowOffset: { width: 0, height: 4 },
        shadowOpacity: 0.22,
        shadowRadius: 12,
        elevation: 12,
      }}>
        <Text style={{
          fontSize: percentageTextSize,
          fontWeight: percentageTextWeight,
          color: percentageTextColor,
        }}>{percentage}</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'center',
    overflow: 'hidden', // Clip content outside the rounded border
  },
});

import React from "react";
import { View, Text, StyleSheet } from "react-native";
import Svg, { Defs, Stop, LinearGradient, Path } from "react-native-svg";
import { Colors } from '@/constants/Colors';

interface CircularGaugeProps {
  percentage: number;
  size?: number;
  strokeWidth?: number;
  pieGradientColors?: string[];
  centerCircleColor?: string;
  percentageTextColor?: string;
  percentageTextSize?: number;
  percentageTextWeight?: 'normal' | 'bold' | '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
}

export default function CircularGauge({
  percentage,
  size = 72,
  strokeWidth = 12,
  pieGradientColors = ['#BD0000', '#FF8282'], // Default gradient colors
  centerCircleColor = Colors.light.background, // Default white
  percentageTextColor = Colors.light.text, // Default dark text
  percentageTextSize = 20, // Default font size
  percentageTextWeight = 'bold', // Default font weight
}: CircularGaugeProps) {
  // 게이지의 중심과 반지름 계산
  const center = size / 2;
  const radius = center - strokeWidth / 2;

  // 파이(피자) 모양 게이지를 위한 각도 계산
  const startAngle = -90;
  const radians = (deg: number) => (deg * Math.PI) / 180;
  const describeArc = (cx: number, cy: number, r: number, startAngle: number, endAngle: number) => {
    const start = {
      x: cx + r * Math.cos(radians(startAngle)),
      y: cy + r * Math.sin(radians(startAngle)),
    };
    const end = {
      x: cx + r * Math.cos(radians(endAngle)),
      y: cy + r * Math.sin(radians(endAngle)),
    };
    const largeArcFlag = endAngle - startAngle <= 180 ? "0" : "1";
    return [
      `M ${cx} ${cy}`,
      `L ${start.x} ${start.y}`,
      `A ${r} ${r} 0 ${largeArcFlag} 1 ${end.x} ${end.y}`,
      "Z",
    ].join(" ");
  };
  const endAngle = startAngle + (percentage / 100) * 360;
  const piePath = describeArc(center, center, radius, startAngle, endAngle);
  const bgPath = describeArc(center, center, radius, endAngle, startAngle + 360);

  return (
    <View style={[styles.container, { width: size, height: size, borderRadius: size / 2 }]}>
      <Svg width={size} height={size} style={{ position: 'absolute' }}>
        <Defs>
          {/* 밝은 부분이 절대적 상단(12시 방향)에 오도록 x1/y1=0.5,0 x2/y2=0.5,1로 설정 */}
          <LinearGradient id="strokeGradient" x1="50%" y1="0%" x2="50%" y2="100%">
            {pieGradientColors.map((color, index) => (
              <Stop key={index} offset={`${(index / (pieGradientColors.length - 1)) * 100}%`} stopColor={color} />
            ))}
          </LinearGradient>
        </Defs>
        {/* 비어있는 부분 배경 (회색) */}
        <Path
          d={bgPath}
          fill="#EFEFEF"
        />
        {/* 파이 모양 게이지 */}
        <Path
          d={piePath}
          fill="url(#strokeGradient)"
        />
      </Svg>
      {/* 중앙 흰색 원 */}
      <View style={{
        position: 'absolute',
        width: size * 0.6,
        height: size * 0.6,
        left: size * 0.2,
        top: size * 0.2,
        borderRadius: size * 0.3,
        backgroundColor: centerCircleColor,
        alignItems: 'center',
        justifyContent: 'center',
        shadowColor: Colors.light.text,
        shadowOffset: { width: 0, height: 4 },
        shadowOpacity: 0.22,
        shadowRadius: 12,
        elevation: 12,
      }}>
        <Text style={{
          fontSize: percentageTextSize,
          fontWeight: percentageTextWeight,
          color: percentageTextColor,
        }}>{percentage}</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'center',
    overflow: 'hidden', // Clip content outside the rounded border
  },
});

import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image } from 'react-native';
import { useRouter } from 'expo-router';

// 네비바 아이콘 예시 (실제 경로에 맞게 수정 필요)
const icons = [
  require('../assets/images/home.png'),
  require('../assets/images/search.png'),
  require('../assets/images/plus.png'),
  require('../assets/images/alert.png'),
  require('../assets/images/user.png'),
];

const labels = ['홈', '검색', '추가', '알림', '내정보'];

export default function CustomNavbar({ activeIndex = 0, onTabPress }: { activeIndex?: number; onTabPress?: (idx: number) => void }) {
  const router = useRouter();
  return (
    <View style={styles.container}>
      {icons.map((icon, idx) => (
        <TouchableOpacity
          key={idx}
          style={[styles.tab, activeIndex === idx && styles.activeTab]}
          onPress={() => {
            if (onTabPress) onTabPress(idx);
            // router.push(...) 등 라우팅 처리 가능
          }}
          activeOpacity={0.7}
        >
          <Image source={icon} style={[styles.icon, activeIndex === idx && styles.activeIcon]} />
          <Text style={[styles.label, activeIndex === idx && styles.activeLabel]}>{labels[idx]}</Text>
        </TouchableOpacity>
      ))}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    backgroundColor: '#fff',
    borderTopLeftRadius: 24,
    borderTopRightRadius: 24,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: -2 },
    shadowOpacity: 0.08,
    shadowRadius: 8,
    elevation: 8,
    paddingBottom: 12,
    paddingTop: 8,
    paddingHorizontal: 8,
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  tab: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 4,
    borderRadius: 16,
  },
  activeTab: {
    backgroundColor: '#F5F5F5',
  },
  icon: {
    width: 28,
    height: 28,
    marginBottom: 2,
    opacity: 0.7,
  },
  activeIcon: {
    opacity: 1,
  },
  label: {
    fontSize: 12,
    color: '#888',
    fontWeight: '500',
  },
  activeLabel: {
    color: '#222',
    fontWeight: 'bold',
  },
});

