const OnboardingItem = ({ item }: { item: typeof onboardingData[0] }) => {
    return (
        <View style={styles.itemContainer}>
            <View style={[styles.iconContainer, { backgroundColor: item.backgroundColor }]}>
                <Text style={styles.icon}>{item.icon}</Text>
            </View>
