const Paginator = ({ data, currentIndex }: { data: any[], currentIndex: number }) => {
    return (
        <View style={styles.pageIndicator}>
            {data.map((item, i) => {
                return <View key={i.toString()} style={[styles.dot, { backgroundColor: i === currentIndex ? item.buttonColor : Colors.light.lightGray }]} />;
            })}
        </View>
    );
};

export default function OnboardingScreen() {
  const router = useRouter();
  const insets = useSafeAreaInsets();
  const [currentIndex, setCurrentIndex] = useState(0);
  const slidesRef = useRef<FlatList>(null);

  const viewableItemsChanged = useRef(({ viewableItems }: { viewableItems: any[] }) => {
    if (viewableItems.length > 0) {
        setCurrentIndex(viewableItems[0].index);
    }
  }).current;

  const viewConfig = useRef({ viewAreaCoveragePercentThreshold: 50 }).current;

  const completeOnboarding = async () => {
    try {
      await AsyncStorage.setItem(ONBOARDING_COMPLETED_KEY, 'true');
      router.replace('/');
    } catch (error) {
      console.error('Error saving onboarding status:', error);
      router.replace('/');
    }
  };

  const handleNext = () => {
    if (currentIndex < onboardingData.length - 1) {
        slidesRef.current?.scrollToIndex({ index: currentIndex + 1 });
    } else {
        completeOnboarding();
    }
  };

  const getItemLayout = (_: any, index: number) => ({
    length: width,
    offset: width * index,
    index,
  });

  const currentItem = onboardingData[currentIndex];

  return (
    <View style={styles.container}>
      <StatusBar barStyle="dark-content" />
      <View style={[styles.header, { top: insets.top + 10 }]}>
        <TextButton
          title="건너뛰기"
          onPress={completeOnboarding}
          color={Colors.light.mediumGray}
          fontSize={16}
        />
      </View>

      <FlatList
        style={{ flex: 1 }}
        data={onboardingData}
        renderItem={({ item }) => <OnboardingItem item={item} />}
        keyExtractor={(item) => item.id.toString()}
        horizontal
        pagingEnabled
        showsHorizontalScrollIndicator={false}
        onViewableItemsChanged={viewableItemsChanged}
        viewabilityConfig={viewConfig}
        ref={slidesRef}
        getItemLayout={getItemLayout}
      />

      <View style={[styles.bottomSection, { paddingBottom: insets.bottom + 20 }]}>
        <Paginator data={onboardingData} currentIndex={currentIndex} />
        <PrimaryButton
          title={currentIndex === onboardingData.length - 1 ? '시작하기' : '다음'}
          onPress={handleNext}
          backgroundColor={currentItem.buttonColor}
          width={width - 60}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    position: 'absolute',
    right: 20,
    zIndex: 10,
    top: 50, // Adjusted for safe area
  },
  itemContainer: {
    width: width,
    flex: 1,
    alignItems: 'center',
    justifyContent: 'flex-end', // Aligns content to the bottom
    paddingHorizontal: 40,
    paddingBottom: 180, // 하단 여백을 늘려서 콘텐츠를 위로 올림
  },
  iconContainer: {
    width: 150,
    height: 150,
    borderRadius: 75,
