      activeTextColor: '#E6E6FF',
    },
    {
      key: 'risk' as const,
      title: '위험',
      count: '1건',
      pillBg: Colors.light.dangerLight,
      pillColor: Colors.light.danger,
      activeBg: '#FF5050',
      activeTextColor: '#FFD9D9',
    },
    {
      key: 'safe' as const,
      title: '안전',
      count: '1건',
      pillBg: Colors.light.secondaryLight,
      pillColor: Colors.light.secondary,
      activeBg: '#2DB67D',
      activeTextColor: '#D9F2E6',
    },
  ];

  return (
    <View>
      <View style={styles.groupsContainer}>
        {groups.map(({ key, title, count, pillBg, pillColor, activeBg, activeTextColor }) => {
          const countValue = parseInt(count);
          const isClickable = interactable && countValue > 0;
          const isSelected = selectedGroup === key;
          const isInactive = selectedGroup && selectedGroup !== key;

          const CardComponent = isClickable ? TouchableOpacity : View;

          return (
            <CardComponent
              key={key}
              style={[
                styles.groupCard,
                isClickable && styles.clickableCard,
                isSelected && { backgroundColor: activeBg },
                isInactive && { opacity: 0.4 },
              ]}
              onPress={isClickable ? () => onGroupPress?.(key) : undefined}
              activeOpacity={isClickable ? 0.8 : 1}
            >
              <View style={[
                styles.pill,
                { backgroundColor: isSelected ? activeTextColor : pillBg }
              ]}>
                <Text style={[
                  styles.pillText,
                  { color: isSelected ? activeBg : pillColor }
                ]}>
                  {title}
                </Text>
              </View>
              <Text style={[
                styles.groupCount,
                !isClickable && countValue === 0 && styles.disabledCount,
                { color: isSelected ? '#fff' : '#222' }
              ]}>
                {count}
              </Text>
            </CardComponent>
          );
        })}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  groupsContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    gap: 12,
    marginVertical: 8,
  },
  groupCard: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: '#fff',
    borderRadius: 20, // 더 둥글게
    paddingVertical: 16,
    marginHorizontal: 4,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.08,
    shadowRadius: 8,
    elevation: 2,
  },
  clickableCard: {
    borderWidth: 1,
    borderColor: 'transparent',
  },
  pill: {
    borderRadius: 999,
    paddingHorizontal: 16,
    paddingVertical: 4,
    marginBottom: 8,
    minWidth: 48,
    alignItems: 'center',
  },
  pillText: {
    fontWeight: 'bold',
    fontSize: 15,
    letterSpacing: 0.5,
  },
  groupCount: {
    fontWeight: 'bold',
    fontSize: 18,
    color: '#222',
    marginTop: 2,
  },
  disabledCount: {
    color: '#999',
  },
});

export default InteractionRiskGroups;

import React from 'react';
import { TouchableOpacity, Text, StyleSheet, ViewStyle, TextStyle } from 'react-native';

interface PrimaryButtonProps {
  title: string;
  onPress: () => void;
  backgroundColor?: string;
  textColor?: string;
  width?: number | string;
  disabled?: boolean;
  style?: ViewStyle;
  textStyle?: TextStyle;
}

export default function PrimaryButton({
  title,
  onPress,
  backgroundColor = '#6366F1',
  textColor = '#FFFFFF',
  width = '100%',
  disabled = false,
  style,
  textStyle
}: PrimaryButtonProps) {
  return (
    <TouchableOpacity
      style={[
        styles.button,
        {
          backgroundColor: disabled ? '#E5E7EB' : backgroundColor,
          width: width,
        },
        style
      ]}
      onPress={onPress}
      disabled={disabled}
      activeOpacity={0.8}
    >
      <Text
        style={[
          styles.buttonText,
          {
            color: disabled ? '#9CA3AF' : textColor,
          },
          textStyle
        ]}
      >
        {title}
      </Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  button: {
    height: 54,
    borderRadius: 27,
    alignItems: 'center',
    justifyContent: 'center',
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
  },
  buttonText: {
    fontSize: 18,
    fontWeight: '600',
  },
});

import React from 'react';
import { TouchableOpacity, Text, StyleSheet, ViewStyle, TextStyle, DimensionValue } from 'react-native';

interface PrimaryButtonProps {
  title: string;
  onPress: () => void;
  backgroundColor?: string;
  textColor?: string;
  width?: DimensionValue;
  disabled?: boolean;
  style?: ViewStyle;
  textStyle?: TextStyle;
}

export default function PrimaryButton({
  title,
  onPress,
  backgroundColor = '#6366F1',
  textColor = '#FFFFFF',
  width = '100%',
  disabled = false,
  style,
  textStyle
}: PrimaryButtonProps) {
  return (
    <TouchableOpacity
      style={[
        styles.button,
        {
          backgroundColor: disabled ? '#E5E7EB' : backgroundColor,
          width: width,
        },
        style
      ]}
      onPress={onPress}
      disabled={disabled}
      activeOpacity={0.8}
    >
      <Text
        style={[
          styles.buttonText,
          {
            color: disabled ? '#9CA3AF' : textColor,
          },
          textStyle
        ]}
      >
        {title}
      </Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  button: {
    height: 54,
    borderRadius: 27,
    alignItems: 'center',
    justifyContent: 'center',
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
  },
  buttonText: {
    fontSize: 18,
    fontWeight: '600',
  },
});

import React from 'react';
import { TouchableOpacity, Text, StyleSheet, ViewStyle, TextStyle } from 'react-native';

interface TextButtonProps {
  title: string;
  onPress: () => void;
  color?: string;
  fontSize?: number;
  fontWeight?: 'normal' | 'bold' | '100' | '200' | '300' | '400' | '500' | '600' | '700' | '800' | '900';
  style?: ViewStyle;
  textStyle?: TextStyle;
}

export default function TextButton({
  title,
  onPress,
  color = '#6B7280',
  fontSize = 16,
  fontWeight = '500',
  style,
  textStyle
}: TextButtonProps) {
  return (
    <TouchableOpacity
      style={[styles.button, style]}
      onPress={onPress}
      activeOpacity={0.6}
    >
      <Text
        style={[
          styles.buttonText,
          {
            color: color,
            fontSize: fontSize,
            fontWeight: fontWeight,
          },
          textStyle
        ]}
      >
        {title}
      </Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  button: {
    paddingVertical: 12,
    paddingHorizontal: 24,
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttonText: {
    textAlign: 'center',
  },
});
