import React, { useEffect, useState } from "react";
import { Stack, useRouter, useSegments } from "expo-router";
import { View, ActivityIndicator } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { Colors } from "@/constants/Colors";
import { DevModeProvider } from "@/contexts/DevModeContext";
import { FamilyDataProvider } from '@/contexts/FamilyDataContext';

const ONBOARDING_COMPLETED_KEY = "onboarding_completed";

function RootLayoutNav() {
  const [isLoading, setIsLoading] = useState(true);
  const [showOnboarding, setShowOnboarding] = useState(false);
  const segments = useSegments();
  const router = useRouter();

  const checkOnboardingStatus = async () => {
    try {
      const onboardingCompleted = await AsyncStorage.getItem(ONBOARDING_COMPLETED_KEY);
      const shouldShowOnboarding = onboardingCompleted !== "true";
      setShowOnboarding(shouldShowOnboarding);

      // 온보딩 상태에 따라 네비게이션
      if (shouldShowOnboarding && segments[0] !== "onboarding") {
        router.replace("/onboarding");
      } else if (!shouldShowOnboarding && segments[0] === "onboarding") {
        router.replace("/");
      }
    } catch (error) {
      console.error("Error checking onboarding status:", error);
      setShowOnboarding(true);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    checkOnboardingStatus();
  }, []);

  // AsyncStorage 변경을 감지하기 위한 interval
  useEffect(() => {
    const interval = setInterval(checkOnboardingStatus, 1000);
    return () => clearInterval(interval);
  }, []);

  if (isLoading) {
    return (
      <View
        style={{
          flex: 1,
          justifyContent: "center",
          alignItems: "center",
          backgroundColor: Colors.background,
        }}
      >
        <ActivityIndicator size="large" color={Colors.primary} />
      </View>
    );
  }

  return (
    <Stack screenOptions={{
      headerShown: false,
      animation: "none",
      presentation: "card",
      animationDuration: 0,
      gestureEnabled: false
    }}>
      <Stack.Screen name="onboarding" />
      <Stack.Screen name="index" />
      <Stack.Screen name="chat" />
      <Stack.Screen name="interaction" />
      <Stack.Screen name="myinfo" />
      <Stack.Screen name="family/[id]" />
    </Stack>
  );
}

export default function RootLayout() {
  return (
    <DevModeProvider>
      <FamilyDataProvider>
        <RootLayoutNav />
      </FamilyDataProvider>
    </DevModeProvider>
  );
}

import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image } from 'react-native';
import { useRouter } from 'expo-router';
import { Colors } from '@/constants/Colors';

export default function LoginScreen() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const router = useRouter();

  const handleLogin = () => {
    // 실제 로그인 로직은 서버 연동 필요
    if (!email || !password) {
      setError('이메일과 비밀번호를 입력하세요.');
      return;
    }
    setError('');
    // 로그인 성공 시 홈으로 이동 (예시)
    router.replace('/');
  };

  return (
    <View style={styles.container}>
      {/* 상단 로고/타이틀 */}
      <Image source={require('@/../assets/images/icon.png')} style={styles.logo} />
      <Text style={styles.title}>로그인</Text>
      <Text style={styles.subtitle}>PillLink에 오신 것을 환영합니다!</Text>
      {/* 입력 폼 */}
      <View style={styles.inputSection}>
        <TextInput
          style={styles.input}
          placeholder="이메일"
          placeholderTextColor={Colors.mediumGray}
          value={email}
          onChangeText={setEmail}
          keyboardType="email-address"
          autoCapitalize="none"
        />
        <TextInput
          style={styles.input}
          placeholder="비밀번호"
          placeholderTextColor={Colors.mediumGray}
          value={password}
          onChangeText={setPassword}
          secureTextEntry
        />
        {error ? <Text style={styles.error}>{error}</Text> : null}
        <TouchableOpacity style={styles.loginButton} onPress={handleLogin}>
          <Text style={styles.loginButtonText}>로그인</Text>
        </TouchableOpacity>
      </View>
      {/* 하단 링크 */}
      <View style={styles.bottomLinks}>
        <TouchableOpacity onPress={() => router.push('/signup')}>
          <Text style={styles.linkText}>회원가입</Text>
        </TouchableOpacity>
        <Text style={styles.linkDivider}>|</Text>
        <TouchableOpacity onPress={() => router.push('/find-password')}>
          <Text style={styles.linkText}>비밀번호 찾기</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

