import React, { useState } from 'react';
import { View, TextInput, StyleSheet, Text, TouchableOpacity, ScrollView, KeyboardAvoidingView, Platform } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons';

const MOCK_RESULTS = [
  '아토르바스틴', '암로디핀', '로사르탄',
  '아스피린', '탐스로신', '멜록시캄', '세티리진',
  '타이레놀', '메트포르민',
];

export default function SearchResultScreen() {
  const [search, setSearch] = useState('');

  // 실제 검색 결과는 search 값에 따라 필터링해서 보여주면 됩니다.
  const filtered = search
    ? MOCK_RESULTS.filter(item => item.includes(search))
    : MOCK_RESULTS;

  return (
    <KeyboardAvoidingView
      style={{ flex: 1, backgroundColor: '#fff' }}
      behavior={Platform.OS === 'ios' ? 'padding' : undefined}
    >
      {/* 상단 바 대체 */}
      <View style={styles.statusBar} />
      {/* 검색창 */}
      <View style={styles.searchBarContainer}>
        <TextInput
          style={styles.searchBar}
          placeholder="복용하시는 약알을 검색해보세요"
          placeholderTextColor="#B0B0B0"
          value={search}
          onChangeText={setSearch}
          returnKeyType="search"
          autoFocus
        />
        <MaterialCommunityIcons name="magnify" size={28} color="#888" style={styles.searchIcon} />
      </View>
      {/* 검색 결과 */}
      <ScrollView contentContainerStyle={styles.suggestionContainer} keyboardShouldPersistTaps="handled">
        <View style={styles.suggestionWrap}>
          {filtered.length === 0 ? (
            <Text style={styles.noResultText}>검색 결과가 없습니다.</Text>
          ) : (
            filtered.map((item) => (
              <TouchableOpacity key={item} style={styles.suggestionButton}>
                <Text style={styles.suggestionText}>{item}</Text>
              </TouchableOpacity>
            ))
          )}
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  statusBar: {
    height: 56,
    backgroundColor: '#1877F2',
    justifyContent: 'flex-end',
  },
  searchBarContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 16,
    marginHorizontal: 16,
    marginBottom: 8,
  },
  searchBar: {
    flex: 1,
    height: 48,
    backgroundColor: '#F7F7F7',
    borderRadius: 24,
    paddingHorizontal: 20,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#D0D0D0',
    color: '#222',
  },
  searchIcon: {
    position: 'absolute',
    right: 20,
    top: 10,
  },
  suggestionContainer: {
    paddingHorizontal: 16,
    paddingTop: 16,
    flexGrow: 1,
  },
  suggestionWrap: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    gap: 12,
    justifyContent: 'flex-start',
  },
  suggestionButton: {
    borderWidth: 1.5,
    borderColor: '#1877F2',
    borderRadius: 24,
    paddingHorizontal: 18,
    paddingVertical: 8,
    marginRight: 8,
    marginBottom: 12,
    backgroundColor: '#fff',
  },
  suggestionText: {
    color: '#1877F2',
    fontSize: 16,
    fontWeight: '500',
  },
  noResultText: {
    color: '#888',
    fontSize: 16,
    marginTop: 32,
    textAlign: 'center',
    width: '100%',
  },
});

