const SUGGESTIONS = [
  '아토르바스틴', '암로디핀', '로사르탄',
  '아스피린', '탐스로신', '멜록시캄', '세티리진',
  '타이레놀', '메트포르민',
];

export default function SearchScreen() {
  const [search, setSearch] = useState('');

  return (
    <KeyboardAvoidingView
      style={{ flex: 1, backgroundColor: '#fff' }}
      behavior={Platform.OS === 'ios' ? 'padding' : undefined}
    >
      {/* 상단 바 대체 */}
      <View style={styles.statusBar} />
      {/* 검색창 */}
      <View style={styles.searchBarContainer}>
        <TextInput
          style={styles.searchBar}
          placeholder="복용하시는 약알을 검색해보세요"
          placeholderTextColor="#B0B0B0"
          value={search}
          onChangeText={setSearch}
          returnKeyType="search"
        />
        <MaterialCommunityIcons name="magnify" size={28} color="#888" style={styles.searchIcon} />
      </View>
      {/* 추천 약물 */}
      <ScrollView contentContainerStyle={styles.suggestionContainer} keyboardShouldPersistTaps="handled">
        <View style={styles.suggestionWrap}>
          {SUGGESTIONS.map((item) => (
            <TouchableOpacity key={item} style={styles.suggestionButton}>
              <Text style={styles.suggestionText}>{item}</Text>
            </TouchableOpacity>
          ))}
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
}

