export default function SignupInfo() {
  const router = useRouter();
  const [name, setName] = useState('');
  const [rrn1, setRrn1] = useState('');
  const [rrn2, setRrn2] = useState('');
  const [phone, setPhone] = useState('');

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => router.back()}>
          <Ionicons name="arrow-back" size={28} color="#222" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>정보입력</Text>
        <View style={{ width: 28 }} />
      </View>
      {/* Progress Bar */}
      <View style={styles.progressBarBg}>
        <View style={[styles.progressBar, { width: '70%' }]} />
      </View>
      {/* Input Fields */}
      <View style={{ marginTop: 32 }}>
        <Text style={styles.labelActive}>이름</Text>
        <TextInput
          style={styles.input}
          placeholder="이름을 입력하세요"
          placeholderTextColor="#C4C4C4"
          value={name}
          onChangeText={setName}
        />
        <Text style={styles.labelActive}>주민등록번호</Text>
        <View style={styles.rrnRow}>
          <TextInput
            style={[styles.input, { flex: 1 }]}
            placeholder=""
            value={rrn1}
            onChangeText={setRrn1}
            keyboardType="number-pad"
            maxLength={6}
          />
          <Text style={styles.rrnDash}>-</Text>
          <TextInput
            style={[styles.input, { flex: 1 }]}
            placeholder=""
            value={rrn2}
            onChangeText={setRrn2}
            keyboardType="number-pad"
            maxLength={7}
            secureTextEntry
          />
        </View>
        <Text style={styles.labelActive}>휴대전화번호</Text>
        <TextInput
          style={styles.input}
          placeholder="ex) 01012345678"
          placeholderTextColor="#C4C4C4"
          value={phone}
          onChangeText={setPhone}
          keyboardType="phone-pad"
        />
      </View>
      {/* Next Button */}
      <TouchableOpacity style={styles.nextBtn} disabled>
        <Text style={styles.nextBtnText}>다음</Text>
      </TouchableOpacity>
    </View>
  );
}

