import React, { useState } from 'react';
import { View, TextInput, Text, StyleSheet, TextInputProps } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { Colors } from '@/constants/Colors';

interface AuthInputProps extends TextInputProps {
  label: string;
  icon?: keyof typeof Ionicons.glyphMap;
  error?: string | null;
  isFocused?: boolean;
}

const AuthInput: React.FC<AuthInputProps> = ({ label, icon, error, isFocused, secureTextEntry, ...props }) => {
  const [showPassword, setShowPassword] = useState(false);
  const isPassword = !!secureTextEntry;

  const iconColor = error ? Colors.danger : isFocused ? Colors.primary : Colors.mediumGray;

  return (
    <View style={styles.container}>
      {!!label && <Text style={styles.label}>{label}</Text>}
      <View
        style={[
          styles.inputContainer,
          isFocused && styles.inputContainerFocused,
          error && styles.inputContainerError,
        ]}
      >
        {icon && <Ionicons name={icon} size={20} color={iconColor} style={styles.icon} />}
        <TextInput
          style={styles.input}
          placeholderTextColor={Colors.placeholder}
          secureTextEntry={isPassword && !showPassword}
          {...props}
        />
        {isPassword && (
          <Ionicons
            name={showPassword ? 'eye-off' : 'eye'}
            size={20}
            color={Colors.mediumGray}
            style={styles.eyeIcon}
            onPress={() => setShowPassword((prev) => !prev)}
          />
        )}
      </View>
      {error && <Text style={styles.errorText}>{error}</Text>}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginBottom: 20,
  },
  label: {
    fontSize: 14,
    fontWeight: '500',
    color: Colors.darkGray,
    marginBottom: 8,
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: Colors.lightGray,
    borderRadius: 14,
    borderWidth: 1.5,
    borderColor: 'transparent',
    paddingHorizontal: 16,
    height: 54,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.06,
    shadowRadius: 2,
  },
  inputContainerFocused: {
    borderColor: Colors.primary,
    backgroundColor: '#fff',
    shadowOpacity: 0.12,
    shadowRadius: 4,
  },
  inputContainerError: {
    borderColor: Colors.danger,
  },
  icon: {
    marginRight: 10,
  },
  input: {
    flex: 1,
    fontSize: 16,
    color: Colors.text,
    paddingVertical: 10,
  },
  eyeIcon: {
    marginLeft: 8,
  },
  errorText: {
    marginTop: 6,
    fontSize: 12,
    color: Colors.danger,
    fontWeight: '500',
  },
});

export default AuthInput;

import React, { useState } from 'react';
import { View, TextInput, Text, StyleSheet, TextInputProps } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { Colors } from '@/constants/Colors';

interface AuthInputProps extends TextInputProps {
  label: string;
  icon?: keyof typeof Ionicons.glyphMap;
  error?: string | null;
  isFocused?: boolean;
}

const AuthInput: React.FC<AuthInputProps> = ({ label, icon, error, isFocused, secureTextEntry, ...props }) => {
  const [showPassword, setShowPassword] = useState(false);
  const isPassword = !!secureTextEntry;

  const iconColor = error ? Colors.danger : isFocused ? Colors.primary : Colors.mediumGray;

  return (
    <View style={styles.container}>
      {!!label && <Text style={styles.label}>{label}</Text>}
      <View
        style={[
          styles.inputContainer,
          isFocused && styles.inputContainerFocused,
          error && styles.inputContainerError,
        ]}
      >
        {icon && <Ionicons name={icon} size={22} color={iconColor} style={styles.icon} />}
        <TextInput
          style={styles.input}
          placeholderTextColor={Colors.placeholder || '#B0B8C1'}
          secureTextEntry={isPassword && !showPassword}
          {...props}
        />
        {isPassword && (
          <Ionicons
            name={showPassword ? 'eye-off' : 'eye'}
            size={20}
            color={Colors.mediumGray}
            style={styles.eyeIcon}
            onPress={() => setShowPassword((prev) => !prev)}
          />
        )}
      </View>
      {error && <Text style={styles.errorText}>{error}</Text>}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginBottom: 22,
  },
  label: {
    fontSize: 15,
    fontWeight: '600',
    color: Colors.darkGray,
    marginBottom: 7,
    marginLeft: 2,
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#F7F8FA',
    borderRadius: 14,
    borderWidth: 1.5,
    borderColor: 'transparent',
    paddingHorizontal: 16,
    height: 54,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.04,
    shadowRadius: 2,
    transitionProperty: 'border-color, box-shadow',
    transitionDuration: '0.2s',
  },
  inputContainerFocused: {
    borderColor: Colors.primary,
    backgroundColor: '#fff',
    shadowOpacity: 0.10,
    shadowRadius: 4,
  },
  inputContainerError: {
    borderColor: Colors.danger,
    backgroundColor: '#FFF6F6',
  },
  icon: {
    marginRight: 10,
  },
  input: {
    flex: 1,
    fontSize: 17,
    color: Colors.text,
    paddingVertical: 10,
    backgroundColor: 'transparent',
  },
  eyeIcon: {
    marginLeft: 8,
  },
  errorText: {
    marginTop: 6,
    fontSize: 13,
    color: Colors.danger,
    fontWeight: '500',
    marginLeft: 2,
  },
});

export default AuthInput;

