import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image } from 'react-native';
import { useRouter } from 'expo-router';

// 네비바 아이콘 예시 (실제 경로에 맞게 수정 필요)
const icons = [
  require('../assets/images/home.png'),
  require('../assets/images/search.png'),
  require('../assets/images/plus.png'),
  require('../assets/images/alert.png'),
  require('../assets/images/user.png'),
];

const labels = ['홈', '검색', '추가', '알림', '내정보'];

export default function CustomNavbar({ activeIndex = 0, onTabPress }: { activeIndex?: number; onTabPress?: (idx: number) => void }) {
  const router = useRouter();
  return (
    <View style={styles.container}>
      {icons.map((icon, idx) => (
        <TouchableOpacity
          key={idx}
          style={[styles.tab, activeIndex === idx && styles.activeTab]}
          onPress={() => {
            if (onTabPress) onTabPress(idx);
            // router.push(...) 등 라우팅 처리 가능
          }}
          activeOpacity={0.7}
        >
          <Image source={icon} style={[styles.icon, activeIndex === idx && styles.activeIcon]} />
          <Text style={[styles.label, activeIndex === idx && styles.activeLabel]}>{labels[idx]}</Text>
        </TouchableOpacity>
      ))}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    backgroundColor: '#fff',
    borderTopLeftRadius: 24,
    borderTopRightRadius: 24,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: -2 },
    shadowOpacity: 0.08,
    shadowRadius: 8,
    elevation: 8,
    paddingBottom: 12,
    paddingTop: 8,
    paddingHorizontal: 8,
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  tab: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 4,
    borderRadius: 16,
  },
  activeTab: {
    backgroundColor: '#F5F5F5',
  },
  icon: {
    width: 28,
    height: 28,
    marginBottom: 2,
    opacity: 0.7,
  },
  activeIcon: {
    opacity: 1,
  },
  label: {
    fontSize: 12,
    color: '#888',
    fontWeight: '500',
  },
  activeLabel: {
    color: '#222',
    fontWeight: 'bold',
  },
});

import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from 'react-native';
import { MaterialIcons, Ionicons } from '@expo/vector-icons';
import { useRouter } from 'expo-router';
import AsyncStorage from '@react-native-async-storage/async-storage';

export function FamilyAvatar({ name, active, style }: { name: string; active?: boolean; style?: any }) {
  return (
    <View style={[styles.circle, active && styles.avatarActive, style]}>
      <Text style={[styles.avatarText, { textAlignVertical: 'center' }]}>{name[0]}</Text>
    </View>
  );
}

const FamilyListItem = ({ item }: { item: any }) => {
  const router = useRouter();

  if (item.type === 'invite') {
    return (
      <TouchableOpacity style={styles.row} activeOpacity={0.7}>
        <View style={styles.inviteCircle}>
          <Ionicons name="add" size={24} color="#4285F4" />
        </View>
        <Text style={styles.inviteText}>초대하기</Text>
      </TouchableOpacity>
    );
  }

  return (
    <TouchableOpacity
      style={styles.row}
      activeOpacity={0.7}
      onPress={() => router.push(`/family/${item.id}`)}
    >
      <FamilyAvatar name={item.name} active={item.active} />
      <Text style={styles.name}>{item.name}</Text>
      <View style={styles.iconContainer}>
        {item.active ? (
          <View style={styles.notificationBadge}>
            <Ionicons name="notifications" size={20} color="#10B981" />
          </View>
        ) : (
          <View style={styles.notificationBadgeOff}>
            <MaterialIcons name="notifications-off" size={20} color="#EF4444" />
          </View>
        )}
      </View>
    </TouchableOpacity>
  );
};

const FamilyGroup = ({ data, showAvatars = true, onSelectMember, selectedId }: { data: any[], showAvatars?: boolean, onSelectMember?: (id: string) => void, selectedId?: string | null }) => {
  const router = useRouter();

  const renderItem = ({ item }: { item: any }) => {
    if (item.type === 'invite') {
      return (
        <TouchableOpacity style={styles.row} activeOpacity={0.7}>
          <View style={styles.inviteCircle}>
            <Ionicons name="add" size={24} color="#4285F4" />
          </View>
          <Text style={styles.inviteText}>초대하기</Text>
        </TouchableOpacity>
      );
    }
    return (
      <View style={styles.row}>
        {showAvatars && (
          <FamilyAvatar name={item.name} active={selectedId === item.id} />
        )}
        <TouchableOpacity
          style={{ flex: 1 }}
          activeOpacity={0.7}
          onPress={() => {
            onSelectMember?.(item.id);
            AsyncStorage.setItem('selected_family_id', item.id);
          }}
        >
          <Text style={styles.name}>{item.name}</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.iconContainer}
          onPress={() => router.push(`/family/${item.id}`)}
          hitSlop={{ top: 10, bottom: 10, left: 10, right: 10 }}
        >
          <MaterialIcons name="settings" size={20} color="#9CA3AF" />
        </TouchableOpacity>
      </View>
    );
  };
  return (
    <View style={styles.container}>
      <FlatList
        data={data}
        renderItem={renderItem}
        keyExtractor={item => item.id}
        ItemSeparatorComponent={() => <View style={styles.separator} />}
        scrollEnabled={false}
      />
    </View>
  );
};

