import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View, Image } from 'react-native';
import { Colors } from '@/constants/Colors';

interface LargeButtonProps {
  label: string;
  onPress: () => void;
  iconType?: 'kakao' | 'google';
}

export default function LargeButton({ label, onPress, iconType }: LargeButtonProps) {
  let icon = null;
  let buttonStyle = [styles.button];
  let textStyle = [styles.text];

  if (iconType === 'kakao') {
    buttonStyle.push(styles.kakaoButton);
    textStyle.push(styles.kakaoText);
    icon = (
      <View style={styles.kakaoIconWrap}><Text style={styles.kakaoIcon}>TALK</Text></View>
    );
  } else if (iconType === 'google') {
    buttonStyle.push(styles.googleButton);
    icon = (
      <Image source={{uri: 'https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg'}} style={styles.googleIcon} />
    );
  }

  return (
    <TouchableOpacity style={buttonStyle} onPress={onPress}>
      {icon}
      <Text style={textStyle}>{label}</Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  button: {
    width: '100%',
    backgroundColor: Colors.primary,
    borderRadius: 20,
    paddingVertical: 32,
    alignItems: 'center',
    flexDirection: 'row',
    justifyContent: 'center',
    marginBottom: 32,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.12,
    shadowRadius: 8,
    elevation: 2,
  },
  text: {
    color: '#fff',
    fontSize: 24,
    fontWeight: 'bold',
  },
  kakaoButton: {
    backgroundColor: '#FEE500',
    marginBottom: 24,
  },
  kakaoText: {
    color: '#3C1E1E',
  },
  kakaoIconWrap: {
    backgroundColor: '#3C1E1E',
    borderRadius: 12,
    paddingHorizontal: 10,
    paddingVertical: 2,
    marginRight: 10,
  },
  kakaoIcon: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 14,
  },
  googleButton: {
    backgroundColor: '#fff',
    borderWidth: 1,
    borderColor: '#E5E7EB',
    marginBottom: 24,
  },
  googleIcon: {
    width: 24,
    height: 24,
    marginRight: 10,
  },
});

