import React from 'react';
import { TouchableOpacity, View, Text, Image, StyleSheet } from 'react-native';

interface SocialLoginButtonProps {
  type: 'kakao' | 'google' | 'guardian' | 'ward';
  onPress: () => void;
}

export default function SocialLoginButton({ type, onPress }: SocialLoginButtonProps) {
  if (type === 'kakao') {
    return (
      <TouchableOpacity style={styles.kakaoButton} onPress={onPress}>
        <View style={styles.kakaoIconWrap}><Text style={styles.kakaoIcon}>TALK</Text></View>
        <Text style={styles.kakaoText}>카카오톡으로 로그인</Text>
      </TouchableOpacity>
    );
  }
  if (type === 'google') {
    return (
      <TouchableOpacity style={styles.googleButton} onPress={onPress}>
        <Image source={{uri: 'https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg'}} style={styles.googleIcon} />
        <Text style={styles.googleText}>Google로 로그인</Text>
      </TouchableOpacity>
    );
  }
  if (type === 'guardian' || type === 'ward') {
    return (
      <TouchableOpacity
        style={styles.roleButton}
        onPress={onPress}
      >
        <Text style={styles.roleButtonText}>{type === 'guardian' ? '보호자' : '보호 대상자'}</Text>
      </TouchableOpacity>
    );
  }
  return null;
}

