export interface MedicationInfo {
  id: string;
  medicationName: string;
  time: string;
  dosage: string;
  enabled: boolean;
  frequency: 'daily' | 'weekly' | 'as-needed';
  notes?: string;
  icon: string;
}

export interface FamilyMember {
  id: string;
  name: string;
  active: boolean;
  phone?: string;
  email?: string;
  medications?: MedicationInfo[];
}

