// 특정 가족 구성원 조회 함수
export const getFamilyMemberById = (id: string): FamilyMember | undefined => {
  return FAMILY_DATA.find(member => member.id === id && member.id !== 'invite');
};

// 초대하기 제외한 실제 가족 구성원만 반환
export const getFamilyMembers = (): FamilyMember[] => {
  return FAMILY_DATA.filter(member => member.id !== 'invite');
};

// 특정 가족 구성원의 약물 정보 조회
export const getMedicationsByMemberId = (memberId: string): MedicationInfo[] => {
  const member = getFamilyMemberById(memberId);
  return member?.medications || [];
};

// 모든 가족의 활성화된 약물 알림 수 조회
export const getActiveMedicationAlarmsCount = (): number => {
  return getFamilyMembers().reduce((count, member) => {
    const activeMedications = member.medications?.filter(med => med.enabled) || [];
    return count + activeMedications.length;
  }, 0);
};

import React, { createContext, useContext, useState } from 'react';
import { FAMILY_DATA } from '@/constants/FamilyData';

// 타입 정의
interface MedicationInfo {
  id: string;
  medicationName: string;
  enabled: boolean;
  [key: string]: any;
}
interface FamilyMember {
  id: string;
  name: string;
  medications?: MedicationInfo[];
  [key: string]: any;
}
interface FamilyDataContextType {
  familyData: FamilyMember[];
  updateMedicationEnabled: (userId: string, medicationName: string, enabled: boolean) => void;
}

const defaultContext: FamilyDataContextType = {
  familyData: FAMILY_DATA,

import React, { createContext, useContext, useState } from 'react';
import { FAMILY_DATA } from '@/constants/FamilyData';

// 타입 정의
interface MedicationInfo {
  id: string;
  medicationName: string;
  enabled: boolean;
  [key: string]: any;
}
interface FamilyMember {
  id: string;
  name: string;
  medications?: MedicationInfo[];
  [key: string]: any;
}
interface FamilyDataContextType {
  familyData: FamilyMember[];
  updateMedicationEnabled: (userId: string, medicationName: string, enabled: boolean) => void;
}

const defaultContext: FamilyDataContextType = {
  familyData: FAMILY_DATA,
  updateMedicationEnabled: () => {},
};

const FamilyDataContext = createContext<FamilyDataContextType>(defaultContext);

export const FamilyDataProvider = ({ children }: { children: React.ReactNode }) => {
  const [familyData, setFamilyData] = useState(FAMILY_DATA);

  // 약물 알림 설정(스위치) 변경 함수
  const updateMedicationEnabled = (userId: string, medicationName: string, enabled: boolean) => {
    setFamilyData(prev => prev.map(user => {
      if (user.id !== userId) return user;
      return {
        ...user,
        medications: user.medications?.map(med =>
          med.medicationName === medicationName ? { ...med, enabled } : med
        )
      };
    }));
  };

  return (
    <FamilyDataContext.Provider value={{ familyData, updateMedicationEnabled }}>
      {children}
    </FamilyDataContext.Provider>
  );
};

export const useFamilyData = () => useContext(FamilyDataContext);
export { FamilyDataContextType };
